import React from "react";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import { connect } from "react-redux";

function CustomChart(props) {
  const [state, setBills] = useState([]);
  const condition = Boolean(Math.round(Math.random()));

  console.log("ChartList", JSON.stringify(props.billList));


  const options = {
    series: [
      {
        data: [
          { x: Date.UTC(2019, 15, 13), y: 25 },
          { x: Date.UTC(2019, 16, 11), y: 35 },
          { x: Date.UTC(2019, 17, 11), y: 40 },
          { x: Date.UTC(2019, 18, 11), y: 20 },
          { x: Date.UTC(2019, 19, 11), y: 15 },
        ],
      },
    ],
    xAxis: {
      type: "datetime",
      dateTimeLabelFormats: {
        day: condition ? "%e-%b-%y" : "%e%b%y",
        month: condition ? "%b-%y" : "%b '%y",
      },
    },
  };

  return (
    <div>
      {}
      <HighchartsReact highcharts={Highcharts} options={options} />
    </div>
  );
}
const mapStateToProps = ({ dashboardReducer }) => {
  const { billList } = dashboardReducer;
  return { billList };
};

export default connect(mapStateToProps, {})(CustomChart);
