import React, { useState, useEffect } from "react";
import "./Dashboard.scss";
import Text from "../common/Text";
import Filter from "../common/Filter";
import { connect } from "react-redux";
import { fetchBills, deleteBill, addBill } from "../../actions/index";
import { Button } from "@material-ui/core";
import List from "../common/List";
import BottomComponent from "../bottomComponent/BottomComponent";
import { Link } from "react-router-dom";
import { MONTHLY_BUDGET } from "../../constants/Constant";

function Dashboard(props) {
  const [state, setBills] = useState({
    filteredBills: [],
    payableBills: [],
  });

  function FilterBillHandler(value) {
    console.log("Value of filter", value);
    if (value) {
      setBills({
        ...state.filteredBills,
        filteredBills: props.billList.filter(
          (element) => element.category === value
        ),
      });
    }
  }

  useEffect(() => {
    props.fetchBills();
  }, []);

  const deleteBillclick = (event, bill) => {
    console.log("Value of item for delete", JSON.stringify(bill));
    props.deleteBill(bill.id);
  };

  const renderBills = (list) => {
    // console.log("List in render bills", JSON.stringify(list));
    return (
      <div>
        <List list={list} deleteClick={deleteBillclick} />
      </div>
    );
  };

  const resetClick = () => {
    setBills({
      ...state.filteredBills,
      filteredBills: [],
    });
    props.fetchBills();
  };

  const addBill = () => {
    props.addBill();
  };

  const minimumBillPay = () => {
    setBills({
      ...state.filteredBills,
      filteredBills: props.billList.sort(
        (a, b) => parseInt(b.amount) - parseInt(a.amount)
      ),
    });

    let totalSum = 0;
    let ids = []
    state.filteredBills.forEach((element) => {
      if (totalSum + parseInt(element.amount) <= MONTHLY_BUDGET) {
        totalSum += parseInt(arg.amount);
        ids.push(element.id);
      }
    });
  };

  return (
    <div>
      <Text
        textStyle={"dashboard-header"}
        text={"Bill DashBoard"}
        textContainerStyle={"dashboard-header-container"}
      />

      <div className="list-action-container">
        <Button onClick={() => resetClick()} variant="outlined" color="primary">
          {"Reset"}
        </Button>

        <Button onClick={() => addBill()} variant="outlined" color="primary">
          {"Add Bill"}
        </Button>

        <Button
          onClick={() => minimumBillPay()}
          variant="outlined"
          color="primary"
        >
          {"Minimum Bill"}
        </Button>
      </div>
      <div className={"graph-name-container"}>
        <Link to="/chart" className="btn btn-primary">
          Graph Analysis
        </Link>
      </div>

      <Filter FilterBillHandler={FilterBillHandler}></Filter>

      {props.billList.length > 0 &&
        renderBills(
          state.filteredBills && state.filteredBills.length > 0
            ? state.filteredBills
            : props.billList
        )}

      <BottomComponent
        list={
          state.filteredBills && state.filteredBills.length > 0
            ? state.filteredBills
            : props.billList
        }
      />
    </div>
  );
}

const mapStateToProps = ({ dashboardReducer }) => {
  const { billFetchError, billList } = dashboardReducer;
  return { billFetchError, billList };
};

export default connect(mapStateToProps, {
  fetchBills,
  deleteBill,
  addBill,
})(Dashboard);
